import "../../App.css"
import Course_img from '../Images/course_curriculam.jpg'
const colleges = require('../Api/api')

const Course =(props)=>{
    const logo_style ={
        width:"5rem"
    }

       const IIT = colleges.IIT[0];
       const fees = IIT.fees_structure;
       const obj = IIT.Subject[props.sub]
       const course = obj.course;
       const course_name = obj.course_name;
       const course_review  = obj.course_review;

    return(

    <div className='d-flex  justify-content-around p-5'>
         <div className=''>
          <img className='course_img' src={Course_img} alt="" />
         </div>
         <div className='course_detail'>
               <h2 className='text-bold'>Course Curriculam</h2>
              <div className=''>
                  <h4>{course_name}</h4>
                  <div>
                  <a className="text-decoration-none text-bold fs-5" href={course}>Course Structure</a>
                  </div>
                  <a className="text-decoration-none text-bold fs-5 text-success" href={fees}>Fees Structure</a>
                  <p className="fs-6">Review : Some text</p>

              </div>
         </div>
    </div>

    )
}
const Placement =()=>{

    return(
    <div className='d-flex  justify-content-between p-5'>
    <div className='course_detail'>
          <h4 className='text-bold'>Course Curriculam</h4>
         <div className=''>
             <h4>{colleges.IIT[0].Subject.Mathematics.course_name}</h4>
             <a className="text-decoration-none text-bold fs-5" href={colleges.IIT[0].Subject.Mathematics.course}>Course Structure</a>
             <p className="fs-6">Review : Some text</p>
         </div>
    </div>
    <div className='course_image'>
     {/* <img className='course_img' src={placement_img} alt="" /> */}
    </div>
</div>
    )
}
export default Course
